import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Home({ match }) {
    const [fileDetails, setFileDetails] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const params = useParams();
    const id = params.id || 'Not Available'; // ตรวจสอบค่าเริ่มต้น

    useEffect(() => {
        const fetchFileDetails = async () => {
            try {
                const response = await axios.get(`http://localhost:3000/files/${match.params.id}`);
                setFileDetails(response.data);
                setLoading(false);
            } catch (err) {
                setError(err.response ? err.response.data : err.message);
                setLoading(false);
            }
        };

        fetchFileDetails();
    }, [match.params.id]);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error: {error.message}</p>;

    return (
        <div>
            <h1>รายละเอียดไฟล์</h1>
            {fileDetails ? (
                <div>
                    <p><strong>ชื่อเต็ม:</strong> {fileDetails.full_name}</p>
                    <p><strong>เรื่อง:</strong> {fileDetails.subject}</p>
                    <p><strong>จาก:</strong> {fileDetails.from_sender}</p>
                    <p><strong>ถึง:</strong> {fileDetails.to_recipient}</p>
                    <p><strong>URL ไฟล์:</strong> <a href={fileDetails.file_url} target="_blank" rel="noopener noreferrer">ดาวน์โหลดไฟล์</a></p>
                    <p><strong>หมายเหตุ:</strong> {fileDetails.notes}</p>
                </div>
            ) : (
                <p>ไม่มีรายละเอียดไฟล์</p>
            )}
        </div>
    );
}

export default Home;
