import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import '../App.css';

function Home() {
  const location = useLocation();
  const [userData, setUserData] = useState(null);
  const [data, setData] = useState([]);
    
  const [user, setUser] = useState(null);

  useEffect(() => {
    // ดึงข้อมูลผู้ใช้จาก localStorage
    const userData = JSON.parse(localStorage.getItem('user'));
    if (userData) {
      setUser(userData);
    } else {
      // ถ้าข้อมูลผู้ใช้ไม่มี ให้ไปที่หน้า Login
      window.location.href = '/login';
    }
  }, []);
  return (
    <div className='css-Home1'>
      <header>
        Welcome, {userData ? userData.name : 'Guest'}
      </header>

      <div className='css-Home'>
        <div className='css-Home2'>
        {user ? (
            <div>
              <h1>Hello, {user.firstname_lastname}</h1>
              {/* แสดงข้อมูลอื่น ๆ ที่ต้องการ */}
            </div>
          ) : (
            <p>Loading...</p>
          )}
          <h2>User Information</h2>

          <table className='user-table'>
            <thead>
              <tr>
                <th>ชื่อ</th>
                <th>เรื่อง</th>
                <th>สถานะ</th>
                <th>ผู้ดำเนินการ</th>
                <th>ระดับ</th>
              </tr>
            </thead>
            <tbody>
              {data.map((item, index) => (
                <tr key={index}>
                  <td>{item.name}</td>
                  <td>{item.subject}</td>
                  <td>{item.status}</td>
                  <td>{item.operator}</td>
                  <td>{item.level}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Home;
