import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import '../App.css';

function Home() {
  const location = useLocation();
  const [userData, setUserData] = useState(null);
  const [data, setData] = useState([]);

  useEffect(() => {
    // ตรวจสอบว่าเรามีข้อมูลผู้ใช้จาก Login หรือไม่
    if (location.state && location.state.user) {
      setUserData(location.state.user);

      // ดึงข้อมูลจากฐานข้อมูล
      axios.get(`/api/userData/${location.state.user.id}`)
        .then(response => {
          setData(response.data);
        })
        .catch(error => {
          console.error('Error fetching user data:', error);
        });
    }
  }, [location.state]);

  return (
    <div className='css-Home1'>
      <header>
        Welcome, {userData ? userData.name : 'Guest'}
      </header>

      <div className='css-Home'>
        <div className='css-Home2'>
          <h2>User Information</h2>

          <table className='user-table'>
            <thead>
              <tr>
                <th>ชื่อ</th>
                <th>เรื่อง</th>
                <th>สถานะ</th>
                <th>ผู้ดำเนินการ</th>
                <th>ระดับ</th>
              </tr>
            </thead>
            <tbody>
              {data.map((item, index) => (
                <tr key={index}>
                  <td>{item.name}</td>
                  <td>{item.subject}</td>
                  <td>{item.status}</td>
                  <td>{item.operator}</td>
                  <td>{item.level}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Home;
