import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../App.css';

function Home() {
  const [userData, setUserData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [selectedDocument, setSelectedDocument] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:3000/documents')
      .then(response => {
        setUserData(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
        setError('Error fetching data');
        setLoading(false);
      });
  }, []);

  const handleEdit = (document) => {
    setSelectedDocument(document);
    setEditMode(true);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSelectedDocument(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:3000/documents/${selectedDocument.id}`, selectedDocument);
      setEditMode(false);
      axios.get('http://localhost:3000/documents')
        .then(response => {
          setUserData(response.data);
        });
    } catch (error) {
      console.error('Error updating document:', error);
      setError('Error updating document');
    }
  };

  if (loading) {
    return <div className='loading'>Loading...</div>;
  }

  if (error) {
    return <div className='error'>{error}</div>;
  }

  return (
    <div className='home-container'>
      <h2>Document Information</h2>
      <table className='user-table'>
        <thead>
          <tr>
            <th>ชื่อ</th>
            <th>เรื่อง</th>
            <th>จาก</th>
            <th>ถึง</th>
            <th>ไฟล์</th>
            <th>หมายเหตุ</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {userData.map(user => (
            <tr key={user.id}>
              <td>{user.full_name}</td>
              <td>{user.subject}</td>
              <td>{user.from_sender}</td>
              <td>{user.to_recipient}</td>
              <td>
                {user.file_url ? <a href={user.file_url} target="_blank" rel="noopener noreferrer">View File</a> : 'No File'}
              </td>
              <td>{user.notes}</td>
              <td>
                <button className='edit-button' onClick={() => handleEdit(user)}>Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editMode && selectedDocument && (
        <div className='edit-form-container'>
          <h3>Edit Document</h3>
          <form onSubmit={handleSubmit} className='edit-form'>
            <div className='form-group'>
              <label>ชื่อ:</label>
              <input
                type='text'
                name='full_name'
                value={selectedDocument.full_name}
                onChange={handleChange}
                required
              />
            </div>
            <div className='form-group'>
              <label>เรื่อง:</label>
              <input
                type='text'
                name='subject'
                value={selectedDocument.subject}
                onChange={handleChange}
                required
              />
            </div>
            <div className='form-group'>
              <label>จาก:</label>
              <input
                type='text'
                name='from_sender'
                value={selectedDocument.from_sender}
                onChange={handleChange}
                required
              />
            </div>
            <div className='form-group'>
              <label>ถึง:</label>
              <input
                type='text'
                name='to_recipient'
                value={selectedDocument.to_recipient}
                onChange={handleChange}
                required
              />
            </div>
            <div className='form-group'>
              <label>หมายเหตุ:</label>
              <textarea
                name='notes'
                value={selectedDocument.notes}
                onChange={handleChange}
              />
            </div>
            <div className='form-actions'>
              <button type="submit" className='submit-button'>Submit</button>
              <button type="button" className='cancel-button' onClick={() => setEditMode(false)}>Cancel</button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}

export default Home;
