import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../App.css';

function Home() {
  const [userData, setUserData] = useState([]);
  const [loading, setLoading] = useState(true); // สถานะสำหรับการโหลด
  const [error, setError] = useState(null); // สถานะสำหรับข้อผิดพลาด

  useEffect(() => {
    axios.get('http://localhost:3000/documents')
      .then(response => {
        setUserData(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
        setError('Error fetching data');
        setLoading(false);
      });
}, []);
 // Array ว่างจะทำให้ useEffect ทำงานแค่ครั้งเดียวเมื่อ component โหลด

  if (loading) {
    return <div>Loading...</div>; // แสดงข้อความขณะกำลังโหลด
  }

  if (error) {
    return <div>{error}</div>; // แสดงข้อความข้อผิดพลาด
  }

  return (
    <div className='css-Home1'>
      <div className='css-Home'>
        <div className='css-Home2'>
          <h2>Document Information</h2>

          <table className='user-table'>
            <thead>
              <tr>
                <th>ชื่อ</th>
                <th>เรื่อง</th>
                <th>จาก</th>
                <th>ถึง</th>
                <th>ไฟล์</th>
                <th>หมายเหตุ</th>
              </tr>
            </thead>
            <tbody>
              {userData.map(user => (
                <tr key={user.id}> {/* ใช้ id หรือค่าอื่นที่ไม่ซ้ำกัน */}
                  <td>{user.full_name}</td>
                  <td>{user.subject}</td>
                  <td>{user.from_sender}</td>
                  <td>{user.to_recipient}</td>
                  <td>
                    {user.file_url ? <a href={user.file_url} target="_blank" rel="noopener noreferrer">View File</a> : 'No File'}
                  </td>
                  <td>{user.notes}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Home;
