import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function EditDocument() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [document, setDocument] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:3000/documents/${id}`)
      .then(response => {
        setDocument(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching document:', error);
        setError('Error fetching document');
        setLoading(false);
      });
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setDocument(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const fetchDocument = async (id) => {
    try {
      const response = await axios.get(`http://localhost:3000/documents/${id}`);
      setDocument(response.data);
    } catch (error) {
      console.error('Error fetching document:', error);
      // แสดงข้อความหรือจัดการกับข้อผิดพลาดที่เกิดขึ้น
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h3>Edit Document</h3>
      <form onSubmit={handleSubmit}>
        <div>
          <label>ชื่อ:</label>
          <input
            type='text'
            name='full_name'
            value={document.full_name}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>เรื่อง:</label>
          <input
            type='text'
            name='subject'
            value={document.subject}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>จาก:</label>
          <input
            type='text'
            name='from_sender'
            value={document.from_sender}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>ถึง:</label>
          <input
            type='text'
            name='to_recipient'
            value={document.to_recipient}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>หมายเหตุ:</label>
          <textarea
            name='notes'
            value={document.notes}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Submit</button>
        <button type="button" onClick={() => navigate('/')}>Cancel</button>
      </form>
    </div>
  );
}

export default EditDocument;
