// src/components/Home.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import '../App.css';

function Home() {
  const [userData, setUserData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:3000/documents')
      .then(response => {
        setUserData(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
        setError('Error fetching data');
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div className='loading'>Loading...</div>;
  }

  if (error) {
    return <div className='error'>{error}</div>;
  }

  return (
    <Container className='home-container'>
      <h2>Document Information</h2>
      <Row>
        <Col>
          <table className='user-table'>
            <thead>
              <tr>
                <th>ชื่อ</th>
                <th>เรื่อง</th>
                <th>จาก</th>
                <th>ถึง</th>
                <th>ไฟล์</th>
                <th>หมายเหตุ</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {userData.map(user => (
                <tr key={user.id}>
                  <td>{user.full_name}</td>
                  <td>{user.subject}</td>
                  <td>{user.from_sender}</td>
                  <td>{user.to_recipient}</td>
                  <td>
                    {user.file_url ? <a href={user.file_url} target="_blank" rel="noopener noreferrer">View File</a> : 'No File'}
                  </td>
                  <td>{user.notes}</td>
                  <td>
                    <Link to={`/edit/${user.id}`} className='edit-button'>Edit</Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </Col>
      </Row>
    </Container>
  );
}

export default Home;
