// UserUpdate.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function UserUpdate() {
  const { id } = useParams(); // รับ ID จาก URL
  const [document, setDocument] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:3000/documents/${id}`)
      .then(response => setDocument(response.data))
      .catch(error => {
        console.error('Error fetching document:', error);
        setError('Error fetching document');
      });
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setDocument(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:3000/documents/${id}`, document);
      navigate('/'); // กลับไปที่หน้า home หลังจากบันทึก
    } catch (error) {
      console.error('Error updating document:', error);
      setError('Error updating document');
    }
  };

  if (error) {
    return <div className='error'>{error}</div>;
  }

  if (!document) {
    return <div>Loading...</div>;
  }

  return (
    <div className='edit-form-container'>
      <h3>Edit Document</h3>
      <form onSubmit={handleSubmit} className='edit-form'>
        <div className='upload2'>
          <label>ชื่อ:</label>
          <input
            type='text'
            name='full_name'
            value={document.full_name}
            onChange={handleChange}
            required
          />
        </div>
        <div className='upload2'>
          <label>เรื่อง:</label>
          <input
            type='text'
            name='subject'
            value={document.subject}
            onChange={handleChange}
            required
          />
        </div>
        <div className='upload2'>
          <label>จาก:</label>
          <input
            type='text'
            name='from_sender'
            value={document.from_sender}
            onChange={handleChange}
            required
          />
        </div>
        <div className='upload2'>
          <label>ถึง:</label>
          <input
            type='text'
            name='to_recipient'
            value={document.to_recipient}
            onChange={handleChange}
            required
          />
        </div>
        <div className='upload2'>
          <label>หมายเหตุ:</label>
          <textarea
            name='notes'
            value={document.notes}
            onChange={handleChange}
          />
        </div>
        <div className='form-actions'>
          <button type="submit" className='submit-button'>Submit</button>
          <button type="button" className='cancel-button' onClick={() => navigate('/')}>Cancel</button>
        </div>
      </form>
    </div>
  );
}

export default UserUpdate;
