import React, { useState } from 'react';
import '../App.css';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';
import { ToastContainer, toast } from 'react-toastify';
import Validation from './UploadValidation';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function UserUpdate() {
    const [values, setValues] = useState({
        full_name: "",
        subject: "",
        from_sender: "",
        to_recipient: "",
        file_url: null, // เริ่มต้นเป็น null
        notes: ""
    });
    const [userData, setUserData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [selectedDocument, setSelectedDocument] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:3000/documents')
          .then(response => {
            setUserData(response.data);
            setLoading(false);
          })
          .catch(error => {
            console.error('Error fetching user data:', error);
            setError('Error fetching data');
            setLoading(false);
          });
      }, []);
    const handleEdit = (document) => {
        setSelectedDocument(document);
        setEditMode(true);
      };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSelectedDocument(prev => ({
            ...prev,
            [name]: value
        }));
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            console.log('Sending data to server:', selectedDocument); // ตรวจสอบข้อมูลที่ส่งไป
            await axios.put(`http://localhost:3000/documents/${selectedDocument.id}`, selectedDocument);
            setEditMode(false);
            const response = await axios.get('http://localhost:3000/documents');
            setUserData(response.data);
        } catch (error) {
            console.error('Error updating document:', error);
            setError('Error updating document');
        }
    };

    if (loading) {
        return <div className='loading'>Loading...</div>;
    }

    if (error) {
        return <div className='error'>{error}</div>;
    }


    return (
        <div>
            <Navbar expand="lg" className="navbar">
                <Container>
                    <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="me-auto">
                            <Nav.Link href="#home">Home</Nav.Link>
                            <Nav.Link href="#link">Link</Nav.Link>
                            <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                                <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                                <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                                <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                                <NavDropdown.Divider />
                                <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
                            </NavDropdown>
                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
            <td>
                <button className='edit-button' onClick={() => handleEdit(user)}>Edit</button>
            </td>

            <div className='upload1'>
                <form onSubmit={handleSubmit} className='edit-form'>
                    <div className='upload2'>
                        <label>ชื่อ-สกุล :</label>
                        <input
                            type='text'
                            name='full_name'
                            value={selectedDocument.full_name}
                            onChange={handleChange}
                            required
                        />

                    </div>
                    <div className='upload2'>
                        <label>เรื่อง :</label>
                        <input
                            type='text'
                            name='subject'
                            value={selectedDocument.subject}
                            onChange={handleChange}
                            required
                        />

                    </div>
                    <div className='upload2'>
                        <label>จาก :</label>
                        <input
                            type='text'
                            name='from_sender'
                            value={selectedDocument.from_sender}
                            onChange={handleChange}
                            required
                        />

                    </div>
                    <div className='upload2'>
                        <label>ถึง :</label>
                        <input
                            type='text'
                            name='to_recipient'
                            value={selectedDocument.to_recipient}
                            onChange={handleChange}
                            required
                        />

                    </div>

                    <div className='upload2'>
                        <label>หมายเหตุ :</label>
                        <textarea
                            name='notes'
                            value={selectedDocument.notes}
                            onChange={handleChange}
                        />
                    </div>
                    <div className='form-actions'>
                        <button type="submit" className='submit-button'>Submit</button>
                        <button type="button" className='cancel-button' onClick={() => setEditMode(false)}>Cancel</button>
                    </div>
                </form>
            </div>
            <ToastContainer />

        </div>
    );
}

export default UserUpdate;
