import React, { useState } from 'react';
import Validation from './LoginValidation';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

function App() {
  const [input, setInput] = useState({
    email: '',
    password: '',
    term: false
  });

  const navigate = useNavigate();
  const [errors, setErrors] = useState({});

  // ฟังก์ชันจัดการการเปลี่ยนแปลงของอินพุต
  const handleInput = e => {
    setInput(prev => ({
      ...prev,
      [e.target.name]: e.target.type === 'checkbox' ? e.target.checked : e.target.value
    }));
  };

  // ฟังก์ชันจัดการการส่งฟอร์ม
  const handleSubmit = e => {
    e.preventDefault();
    const validationErrors = Validation(input);
    setErrors(validationErrors);

    const hasErrors = Object.values(validationErrors).some(error => error !== "");

    if (!hasErrors && input.term) {
        axios.post('http://localhost:3000/login', input)
            .then(res => {
                if (res.data === "Success") {
                    navigate('/home');
                } else {
                    alert("Invalid email or password");
                }
            })
            .catch(err => {
                console.error("Error:", err.response ? err.response.data : err.message);
                alert("เกิดข้อผิดพลาดในการเข้าสู่ระบบ");
            });
    } else if (!input.term) {
        alert("กรุณายอมรับเงื่อนไขและข้อกำหนด");
    }
};


  return (
    <div >
    
      <div className='css-login'>
        <div className='css-login1'>
          <form onSubmit={handleSubmit}>
            <div className='css-login3'>
              <header className='Header'>
                LOGIN
              </header>
              <input
                type='email'
                placeholder='Enter-Email'
                onChange={handleInput}
                name='email'
                className='css-login2'
              />
              {errors.email && <span className='text-danger'>{errors.email}</span>}
              <input
                type='password'
                placeholder='Enter-Password'
                onChange={handleInput}
                name='password'
                className='css-login2'
              />
              {errors.password && <span className='text-danger'>{errors.password}</span>}

              <div className="div-container">
                <input
                  type="checkbox"
                  className="Text"
                  name="term"
                  onChange={handleInput}
                />
                <label className="Text1">
                  Accept term and conditions
                </label>
              </div>
              <div className="button-container">
                <button type="submit" className="btn btn-primary">
                  Login
                </button>

                <Link to='/register' className='btn btn-info register-link'>
                  Register
                </Link>
              </div>

            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default App;
