import React, { useEffect, useState } from 'react';
import '../App.css';

function Home() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // ดึงข้อมูลผู้ใช้จาก localStorage
    const userData = JSON.parse(localStorage.getItem('user'));
    if (userData) {
      setUser(userData);
    } else {
      // ถ้าข้อมูลผู้ใช้ไม่มี ให้ไปที่หน้า Login
      window.location.href = '/';
    }
  }, []);

  return (
    <div className='css-Home1'>
      <header>
        WELCOME TO ...
      </header>
      <div className='css-Home'>
        <div className='css-Home2'>
          {user ? (
            <div>
              <h1>Hello,gfdddddddd {user.firstname_lastname}</h1>
              {/* แสดงข้อมูลอื่น ๆ ที่ต้องการ */}
            </div>
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Home;






































