import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import '../App.css';

function Home() {




  return (
    <div className='css-Home1'>
     

      <div className='css-Home'>
        <div className='css-Home2'>
          
        
          <h2>User Information</h2>

          <table className='user-table'>
            <thead>
              <tr>
                <th>ชื่อ</th>
                <th>เรื่อง</th>
                <th>สถานะ</th>
                <th>ผู้ดำเนินการ</th>
                <th>ระดับ</th>
              </tr>
            </thead>
            <h1>รายละเอียดไฟล์</h1>
            {fileDetails ? (
                <div>
                    <p><strong>ชื่อเต็ม:</strong> {fileDetails.full_name}</p>
                    <p><strong>เรื่อง:</strong> {fileDetails.subject}</p>
                    <p><strong>จาก:</strong> {fileDetails.from_sender}</p>
                    <p><strong>ถึง:</strong> {fileDetails.to_recipient}</p>
                    <p><strong>URL ไฟล์:</strong> <a href={fileDetails.file_url} target="_blank" rel="noopener noreferrer">ดาวน์โหลดไฟล์</a></p>
                    <p><strong>หมายเหตุ:</strong> {fileDetails.notes}</p>
                </div>
            ) : (
                <p>ไม่มีรายละเอียดไฟล์</p>
            )}
          </table>
        </div>
      </div>
    </div>
  );
}

export default Home;
