import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../App.css';

function Home() {
  const [userData, setUserData] = useState([]);

  useEffect(() => {
    // ดึงข้อมูลจาก API เมื่อ component โหลด
    axios.get('http://localhost:3000/users') // ปรับ URL ตาม API ของคุณ
      .then(response => {
        setUserData(response.data);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      });
  }, []); // Array ว่างจะทำให้ useEffect ทำงานแค่ครั้งเดียวเมื่อ component โหลด

  return (
    <div className='css-Home1'>
      <div className='css-Home'>
        <div className='css-Home2'>
          <h2>User Information</h2>

          <table className='user-table'>
            <thead>
              <tr>
                <th>ชื่อ</th>
                <th>เรื่อง</th>
                <th>สถานะ</th>
                <th>ผู้ดำเนินการ</th>
                <th>ระดับ</th>
              </tr>
            </thead>
            <tbody>
              {userData.map(user => (
                <tr key={user.id}> {/* ใช้ id หรือค่าอื่นที่ไม่ซ้ำกัน */}
                  <td>{user.name}</td>
                  <td>{user.subject}</td>
                  <td>{user.status}</td>
                  <td>{user.operator}</td>
                  <td>{user.level}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Home;
