import React, { useEffect, useState } from 'react';
import '../App.css';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';
import { ToastContainer, toast } from 'react-toastify';
import { redirect, useParams } from 'react-router-dom';

function UserUpdate() {
    const [values, setValues] = useState({
        full_name: "",
        subject: "",
        from_sender: "",
        to_recipient: "",
        file_url: null, // เริ่มต้นเป็น null
        notes: ""
    });


    const {id} = useParams();
    const [userData, setUserData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [editMode, setEditMode] = useState(false);
    const [selectedDocument, setSelectedDocument] = useState(null);

    useEffect(() => {

        var requestOption = {
         method: 'GET',
         redirect:'follow'
        };

        axios.get('http://localhost:3000/documents'+id,requestOption)
        .then(response => response.json() )
        .then(result => {
            if(result['status'] === 'ok'){
                setfull_name(result['documents'],['full_name'])
                setsubject(result['documents'],['subject'])
                setfrom_sender(result['documents'],['from_sender'])
                setto_recipient(result['documents'],['to_recipient'])
                setnotes(result['documents'],['notes'])
            }
        })
        .catch(error => console.log('error',error));
       
    }, [id]);

    const handleEdit = (document) => {
        setSelectedDocument(document);
        setEditMode(true);
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSelectedDocument(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            console.log('Sending data to server:', selectedDocument); // ตรวจสอบข้อมูลที่ส่งไป
            await axios.put(`http://localhost:3000/documents/${selectedDocument.id}`, selectedDocument);
            setEditMode(false);
            const response = await axios.get('http://localhost:3000/documents');
            setUserData(response.data);
        } catch (error) {
            console.error('Error updating document:', error);
            setError('Error updating document');
        }
    };

    if (loading) {
        return <div className='loading'>Loading...</div>;
    }

    if (error) {
        return <div className='error'>{error}</div>;
    }

    return (
        <div>
            {/* แสดงรายการเอกสาร */}
            <div className='user-table'>
                {userData.map(document => (
                    <div key={document.id}>
                        <button className='edit-button' onClick={() => handleEdit(document)}>Edit</button>
                    </div>
                ))}
            </div>

            {editMode && (
                <div className='upload1'>
                    <form onSubmit={handleSubmit} className='edit-form'>
                        <div className='upload2'>
                            <label>ชื่อ-สกุล :</label>
                            <input
                                type='text'
                                name='full_name'
                                value={selectedDocument.full_name}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className='upload2'>
                            <label>เรื่อง :</label>
                            <input
                                type='text'
                                name='subject'
                                value={selectedDocument.subject}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className='upload2'>
                            <label>จาก :</label>
                            <input
                                type='text'
                                name='from_sender'
                                value={selectedDocument.from_sender}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className='upload2'>
                            <label>ถึง :</label>
                            <input
                                type='text'
                                name='to_recipient'
                                value={selectedDocument.to_recipient}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className='upload2'>
                            <label>หมายเหตุ :</label>
                            <textarea
                                name='notes'
                                value={selectedDocument.notes}
                                onChange={handleChange}
                            />
                        </div>
                        <div className='form-actions'>
                            <button type="submit" className='submit-button'>Submit</button>
                            <button type="button" className='cancel-button' onClick={() => setEditMode(false)}>Cancel</button>
                        </div>
                    </form>
                </div>
            )}

            <ToastContainer />
        </div>
    );
}

export default UserUpdate;
